def thumb(img):
    """ Returns a thumbnail path from img path"""
    (base,ext) = img.rsplit(".", 1)
    return ".".join([base, "140x100", ext])

def image_html(image):
    out = "<div class=\"image\">\n"
    out += "<a href=\"" + image['image'] + "\">\n"
    out += "<img src=\"" + thumb(image['image']) + "\"/>\n"
    out += """</a>\n"""
    out += """</div>\n"""
    return out


def htmlify(txt):
    return txt.replace("\n","<br/>")

def entry_html(ent):
    out = """<div class="post">\n"""
    out += "<h2>" + ent['created'] + ": " + ent['title'] + "</h2>\n" 
    out += """<div class="text">\n"""
    out += htmlify(ent['text']) + "\n"
    out += """</div>\n"""


    out += """<div class="pics">\n"""
    out += "\n".join(map(image_html, ent['images']))
    out += """</div>\n\n"""
    return out

def log_entry_html(le):
    out = "<tr>"
    out += "<td>" + str(le['date']) + "</td>"
    out += "<td>" + str(le['destination']) + "</td>"
    out += "<td>" + str(le['distance']) + "km </td>"
    out += "<td>" + str(le['climb']) + "m </td>"
    out += "</tr>"
    return out

def logs_html(logs):
    out = "<h3>" + logs['title'] + "</h3>\n"
    out += "<a href=\"" + "blogpics/" + logs['title'] + ".gpx\">GPX</a>"
    out += "<table>\n"
    out += "\n".join(map(log_entry_html, logs['entries']))
    out += "</table>"
    return out

